    /**
     * Convert spherical coordinate to cartesian
     */
    function to3D(pt) {
        var lon = pt[0] * Math.PI / 180;
        var lat = pt[1] * Math.PI / 180;
        var x = Math.cos(lat) * Math.cos(lon);
        var y = Math.cos(lat) * Math.sin(lon);
        var z = Math.sin(lat);
        return [x,y,z];
    }

