                pt[0] = Math.min(Math.max(ex, this.bbox.min[0]), this.bbox.max[0]);
                pt[1] = Math.min(Math.max(ey, this.bbox.min[1]), this.bbox.max[1]);
                pt[2] = Math.min(Math.max(ez, this.bbox.min[2]), this.bbox.max[2]);

