            var lonStep = (this.geoBound.getEast() - this.geoBound.getWest()) / (size - 1);
            var latStep = (this.geoBound.getSouth() - this.geoBound.getNorth()) / (size - 1);
            var radius = this.config.coordinateSystem.getGeoide().getRadius();
