            fragmentShader += "varying vec2 texCoord; \n";
            fragmentShader += "uniform sampler2D texture; \n";
            fragmentShader += "uniform float alpha; \n";
            fragmentShader += "uniform vec3 color; \n";
            fragmentShader += "\n";
            fragmentShader += "void main(void) \n";
